import React from "react";
import { Container, Row, Col, Card, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import './SuccessScreen.css'; // Import CSS file for animation

function SuccessScreen() {
  const navigate = useNavigate();

  const handleBackToHome = () => {
    navigate("/"); // Navigate to the home screen or any other desired route
  };

  return (
    <Container className="py-5">
      <Row className="justify-content-md-center">
        <Col md={8}>
          <Card className="shadow-lg p-4 rounded text-center">
            <div className="checkmark-container">
              <div className="checkmark draw"></div>
            </div>
            <h1 className="text-success mb-4">Payment Confirmed!</h1>
            <p className="lead">
              Your payment via <strong>Cash on Hands</strong> has been successfully processed.
              We will process your order soon.
            </p>
            <Button
              variant="primary"
              className="btn-lg mt-4"
              onClick={handleBackToHome}
            >
              Back to Home
            </Button>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default SuccessScreen;
