import React, { useState } from "react";
import { Button, Container, Row, Col, Form, Card } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

function CashOnDeliveryConfirmationScreen() {
  const [paymentMethod, setPaymentMethod] = useState("");
  const [codDetails, setCodDetails] = useState({
    name: "",
    phone: "",
    email: "",
    address: "",
  });
  const navigate = useNavigate();

  const handleContinue = () => {
    if (paymentMethod === "eSewa") {
      navigate("/esewaVerify");
    } else if (paymentMethod === "COD") {
      console.log("COD Details:", codDetails);
      navigate("/orderSuccess");
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCodDetails({ ...codDetails, [name]: value });
  };

  return (
    <Container className="py-5">
      <Row className="justify-content-md-center">
        <Col md={8}>
          <Card className="shadow-lg p-4 rounded">
            <h1 className="text-center mb-4">Select Payment Method</h1>
            <Form>
              <Form.Group controlId="paymentMethod">
                <Form.Label className="fw-bold">Payment Method</Form.Label>
                <Form.Select
                  value={paymentMethod}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                  className="mb-4"
                  size="lg"
                >
                  <option value="">Select...</option>
                  <option value="eSewa">eSewa</option>
                  <option value="COD">Cash on Hands</option>
                </Form.Select>
              </Form.Group>

              {paymentMethod === "COD" && (
                <div className="my-4">
                  <h2 className="mb-4">Cash on Hands Details</h2>
                  <Form.Group controlId="codName" className="my-3">
                    <Form.Label className="fw-bold">Name</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter your name"
                      name="name"
                      value={codDetails.name}
                      onChange={handleInputChange}
                      className="p-3"
                      required
                    />
                  </Form.Group>

                  <Form.Group controlId="codPhone" className="my-3">
                    <Form.Label className="fw-bold">Phone Number</Form.Label>
                    <Form.Control
                      type="tel"
                      placeholder="Enter your phone number"
                      name="phone"
                      value={codDetails.phone}
                      onChange={handleInputChange}
                      className="p-3"
                      required
                    />
                  </Form.Group>

                  <Form.Group controlId="codEmail" className="my-3">
                    <Form.Label className="fw-bold">Email</Form.Label>
                    <Form.Control
                      type="email"
                      placeholder="Enter your email"
                      name="email"
                      value={codDetails.email}
                      onChange={handleInputChange}
                      className="p-3"
                      required
                    />
                  </Form.Group>

                  <Form.Group controlId="codAddress" className="my-3">
                    <Form.Label className="fw-bold">Address</Form.Label>
                    <Form.Control
                      as="textarea"
                      rows={4}
                      placeholder="Enter your address"
                      name="address"
                      value={codDetails.address}
                      onChange={handleInputChange}
                      className="p-3"
                      required
                    />
                  </Form.Group>
                </div>
              )}

              <Button
                type="button"
                className="w-100 btn-lg my-3 fw-bold"
                disabled={!paymentMethod || (paymentMethod === "COD" && !codDetails.name)}
                onClick={handleContinue}
                variant="primary"
              >
                Continue
              </Button>
            </Form>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default CashOnDeliveryConfirmationScreen;
