import React, { useState, useEffect } from 'react';

function CashOnDeliveryConfirmationScreen() {
  const [loading, setLoading] = useState(true);
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    // Simulate an API call or some processing
    setTimeout(() => {
      setLoading(false);
      setSuccess(true);
    }, 2000); // 2-second delay to mimic loading
  }, []);

  return (
    <div>
      {loading && <h2>Processing your order...</h2>}
      {!loading && success && <h2>Order placed successfully! Cash on Delivery confirmed.</h2>}
    </div>
  );
}

export default CashOnDeliveryConfirmationScreen;
