import React, { useState, useEffect } from 'react';

function EsewaVerificationScreen() {
  const [loading, setLoading] = useState(true);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Simulate verification after eSewa redirects the user back to your site
    const queryParams = new URLSearchParams(window.location.search);
    const transactionId = queryParams.get('oid');
    const amount = queryParams.get('amt');
    const referenceId = queryParams.get('refId');

    if (transactionId && amount && referenceId) {
      // Simulate a verification process using eSewa's API
      verifyPayment(transactionId, amount, referenceId);
    } else {
      setLoading(false);
      setError('Transaction details missing');
    }
  }, []);

  const verifyPayment = (transactionId, amount, referenceId) => {
    // Simulate an API call to verify payment using eSewa's backend API
    setTimeout(() => {
      // Replace this with actual API verification logic
      const isPaymentSuccessful = true; // Simulating successful payment

      setLoading(false);
      if (isPaymentSuccessful) {
        setSuccess(true);
      } else {
        setError('Payment verification failed');
      }
    }, 2000); // Simulate 2 seconds for API verification
  };

  return (
    <div>
      {loading && <h2>Verifying payment with eSewa...</h2>}
      {!loading && success && <h2>Payment verified successfully! Thank you for your purchase.</h2>}
      {!loading && error && <h2>{error}</h2>}
    </div>
  );
}

export default EsewaVerificationScreen;
